using MailKit.Net.Smtp;
using MimeKit;
using TecnoCredito.Models.DTOs;
using TecnoCredito.Services.Interfaces;

namespace TecnoCredito.Services;

public class EmailService(IConfiguration configuration) : IEmailSender
{
  private readonly IConfiguration _configuration = configuration;
  private static string TitleEmail => "Obra Social Provincia";
  public async Task SendEmailAsync(string email, string subject, string message, string senderName, bool generateLogo = false)
  {
    var config = new EmailDTO();
    _configuration.GetSection("EmailSender").Bind(config);

    using var mimeMessage = new MimeMessage();
    mimeMessage.From.Add(new MailboxAddress(TitleEmail, config.UserName));
    mimeMessage.To.Add(new MailboxAddress(senderName, email));
    mimeMessage.Subject = subject;

    var builder = new BodyBuilder
    {
      HtmlBody = message
    };

    if (generateLogo)
    {
      var imageResource = builder.LinkedResources.Add("logo.png", GetLogoAsBytes(), new ContentType("image", "png"));
      imageResource.ContentId = "logo.png"; //MimeUtils.GenerateMessageId()
    }
    mimeMessage.Body = builder.ToMessageBody();

    using var smtp = new SmtpClient();
    await smtp.ConnectAsync(config.Host, config.Port);

    try
    {
      await smtp.AuthenticateAsync(config.UserName, config.Password);
      await smtp.SendAsync(mimeMessage);
    }
    catch (Exception ex)
    {
      throw new Exception(ex.Message, ex);
    }
    finally
    {
      await smtp.DisconnectAsync(true);
    }
  }

  public byte[] GetLogoAsBytes()
  {
    var imageBytes = Convert.FromBase64String(ImageString);
    return imageBytes;
  }

  public string GetLogoAsString()
  {
    return ImageString;
  }

  private static readonly string ImageString = @"";

}
